class V1::ItunesReceiptsController < V1::BaseController
  before_action -> { doorkeeper_authorize! :write }

  api :POST, '/itunes_receipts', 'Upload an iTunes Receipt'
  param_group :error, V1::BaseController
  param :itunes_receipt, Hash, desc: 'Contains a Base64 iTunes Receipt', required: true do
    param :receipt, String, required: true
  end
  formats ['json']
  example '
    {
      "itunes_receipt": {
        "receipt": ""
      }
    }
  '
  def create
    @itunes_receipt = current_resource_owner.itunes_receipts.new(params[:receipt])

    if @itunes_receipt.save
      render json: @itunes_receipt, status: :created
    else
      render json: @itunes_receipt.errors, status: :unprocessable_entity
    end
  end

end
